<ul {...$$restProps}
    class="{$$restProps.class ?? ''} breadcrumb"
    class:border>
  {#each items as { text, href }}
    <li>
      {#if href}
        <a {href}>{text}</a>
      {:else}
        {text}
      {/if}
    </li>
  {/each}
</ul>

<script >export let border = false;
export let items = [];
</script>
