<label class="paper-radio">
  <input type="radio"
         {value}
         {...$$restProps}
         bind:group
         on:focus
         on:blur
         on:change />
  <span>
    {#if $$slots.default}
      <slot />
    {:else}
      {label}
    {/if}
  </span>
</label>

<script >export let label = '';
export let value = '';
export let group = '';
</script>

<style >input[disabled] + span::before, input[disabled] ~ span {
  opacity: 0.5;
  cursor: not-allowed;
}</style>
