<Button {size} {type}
        isLink {disabled}
        aria-label={ariaLabel}
        on:click={clickHandler}>
  {#if label}
    {label}
  {:else}
    <Arrow {size}
      type={action === 'next' ? 'right' : 'left'} />
  {/if}
</Button>

<script >import Button from '../Button.svelte';
import Arrow from './Arrow.svelte';
;
export let size;
export let label;
export let action;
export let type;
export let disabled;
export let clickHandler;
export let ariaLabel;
</script>
