<div {...$$restProps}
     { ...{[`popover-${position}`]: label} }
     class={`${$$restProps.class} ${bgClass(background)}`}
     class:inline
     class:active={always || active}>
  <slot />
</div>

<script >;
export let label = '';
export let position = 'top';
export let inline = true;
export let background = 'primary';
export let active = false;
export let always = false;
function bgClass(color) {
    return `popover-${color}`;
}
</script>

<style >.inline {
  display: inline;
}

[popover-top].popover-primary::after, [popover-right].popover-primary::after,
[popover-bottom].popover-primary::after, [popover-left].popover-primary::after {
  background-color: var(--primary);
}

[popover-top].popover-secondary::after, [popover-right].popover-secondary::after,
[popover-bottom].popover-secondary::after, [popover-left].popover-secondary::after {
  background-color: var(--secondary);
}

[popover-top].popover-success::after, [popover-right].popover-success::after,
[popover-bottom].popover-success::after, [popover-left].popover-success::after {
  background-color: var(--success);
}

[popover-top].popover-warning::after, [popover-right].popover-warning::after,
[popover-bottom].popover-warning::after, [popover-left].popover-warning::after {
  background-color: var(--warning);
}

[popover-top].popover-danger::after, [popover-right].popover-danger::after,
[popover-bottom].popover-danger::after, [popover-left].popover-danger::after {
  background-color: var(--danger);
}

[popover-top], [popover-right],
[popover-bottom], [popover-left] {
  margin: 0;
}
[popover-top].active::after, [popover-right].active::after,
[popover-bottom].active::after, [popover-left].active::after {
  opacity: 1;
}</style>
