<div {...$$restProps}
     class={`progress ${$$restProps.class ?? ''}`}
     class:margin-bottom={marginBottom}>
  <div class={`bar ${type} w-${cappedValue}`}
       class:striped class:infinite>
    {#if $$slots.default}
      <slot />
    {:else if showValue}
      {cappedValue}%
    {/if}
  </div>
</div>

<script >;
export let value = 50;
export let type = 'primary';
export let showValue = false;
export let striped = false;
export let infinite = false;
export let marginBottom = true;
let cappedValue;
$: cappedValue = Math.max(Math.min(+value, 100), 0);
</script>

<style>
.bar.infinite {
  position: relative;
  animation: in-progress 3s infinite;
}

@keyframes in-progress {
  from {
    right: 100%;
  }
  to {
    right: -100%;
  }
}
</style>
